mapper.update:
update reply set
reply = #{reply}, updatedate = sysdate
where rno = #{rno}

mapper.select:
select rno, bno, reply, replyer, replydate, updatedate from ( select /*+Index_asc(reply pk_reply) */ rownum rn, rno, bno, reply, replyer, replydate, updatedate from reply where
rownum <= #{cri.pageNum} * #{cri.amount} and bno = #{bno}) where rn > (#{cri.pageNum} - 1) * #{cri.amount}
order by rno asc
